conbody.codeblock:
myQuery := gocb.NewN1qlQuery("SELECT * FROM default")
rows, err := myBucket.ExecuteN1qlQuery(myQuery)
if err != nil {
  fmt.Printf("N1QL query error: %s\n", err)
}

var row interface{}
for rows.Next(&row) {
    fmt.Printf("Row: %+v\n", row)
}
if err := rows.Close(); err != nil {
    fmt.Printf("N1QL query error: %s\n", err)
}
